    public static ArrayList<Pertunjukan> getListData(){
        Pertunjukan pertunjukan = null;
        ArrayList<Pertunjukan> list = new ArrayList<>();
        for (String[] aData : data) {
            pertunjukan = new Pertunjukan();
            pertunjukan.setJudul(aData[0]);
            pertunjukan.setDaerah(aData[1]);
            pertunjukan.setLokasi(aData[2]);
            pertunjukan.setTanggal(aData[3]);
            pertunjukan.setPhoto(aData[4]);
            pertunjukan.setHarga_reg(aData[5]);
            pertunjukan.setHarga_vip(aData[6]);
            pertunjukan.setDeskripsi(aData[7]);
            pertunjukan.setShortDeskripsi(aData[8]);
            list.add(pertunjukan);
        }

        return list;
    }

    public static  ArrayList<Pertunjukan> getListPopular(){
        Pertunjukan pertunjukan = null;
        ArrayList<Pertunjukan> list = new ArrayList<>();
        ArrayList<String> tempatPopular = new ArrayList<>();
        for(int i = 0 ; i < data.length ; i ++ ){
            String tempat = data[i][1];
            if(Arrays.asList(tempatPopular).contains(tempat)) continue;
            pertunjukan = new Pertunjukan();
            pertunjukan.setJudul(data[i][0]);
            pertunjukan.setDaerah(data[i][1]);
            pertunjukan.setLokasi(data[i][2]);
            pertunjukan.setTanggal(data[i][3]);
            pertunjukan.setPhoto(data[i][4]);
            pertunjukan.setHarga_reg(data[i][5]);
            pertunjukan.setHarga_vip(data[i][6]);
            pertunjukan.setDeskripsi(data[i][7]);
            pertunjukan.setShortDeskripsi(data[i][8]);
            tempatPopular.add(tempat);
            list.add(pertunjukan);
        }
        return list;
    }

